<?php session_start(); require_once "../config/database.php"; require_once "../includes/functions.php"; require_once "../includes/header.php"; requireAdmin(); $page_title = "Enrollment Management - Admin"; $sql = "SELECT e.*, c.title as course_title, u.name as student_name FROM enrollments e JOIN courses c ON e.course_id = c.id JOIN users u ON e.student_id = u.id ORDER BY e.enrollment_date DESC"; $enrollments = $conn->query($sql); ?> <div class="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8"><div class="px-4 py-6 sm:px-0"><h1 class="text-3xl font-bold text-gray-900">Enrollment Management</h1></div><div class="bg-white shadow overflow-hidden sm:rounded-md"><div class="px-4 py-5 sm:px-6"><h3 class="text-lg leading-6 font-medium text-gray-900">All Enrollments (<?php echo $enrollments->num_rows; ?> total)</h3></div><ul class="divide-y divide-gray-200"><?php while ($enrollment = $enrollments->fetch_assoc()): ?><li class="px-4 py-4"><div class="flex items-center justify-between"><div><h4 class="text-lg font-medium text-gray-900"><?php echo htmlspecialchars($enrollment["student_name"]); ?></h4><p class="text-sm text-gray-600"><?php echo htmlspecialchars($enrollment["course_title"]); ?></p></div></div></li><?php endwhile; ?></ul></div></div></body></html>
